import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { registerUser } from "../../redux/Slices/authenticationSlice";

export default function SignIn(){
	let navigate = useNavigate();
	const dispatch = useDispatch();
	const [credentials, setcredentials] = useState({username:"",email:"",password:""});
	const { error, error_message, success_message } = useSelector((state) => state.authentication);

	useEffect(() => {
        // redirect to home if already logged in
        if (success_message !== '') navigate("/sign-in", { replace: true });
    }, [success_message]);

	const handleSubmit = async (e) =>{
        e.preventDefault();
        const {username, email, password} = credentials;
		dispatch(registerUser({ username, email, password}));
    }
    const onChange = (e) =>{
        setcredentials({...credentials,[e.target.name]:e.target.value});
      }

	return(
		<div class="limiter">
			<div class="container-login100">
				<div class="wrap-login100">
					<div class="login100-pic js-tilt" data-tilt>
						<img src="./assets/icon/banner.jpeg" alt="IMG" />
					</div>

					<form class="login100-form validate-form" onSubmit={handleSubmit}>
						<span class="login100-form-title">
							Member Sign Up
						</span>

						<div class="wrap-input100 validate-input" data-validate = "Valid email is required: ex@abc.xyz">
							<input class="input100" type="text" name="email" placeholder="Email" />
							<span class="focus-input100"></span>
							<span class="symbol-input100">
								<i class="fa fa-envelope" aria-hidden="true"></i>
							</span>
						</div>

						<div class="wrap-input100 validate-input" data-validate = "Password is required">
							<input class="input100" type="password" name="pass" placeholder="Password" />
							<span class="focus-input100"></span>
							<span class="symbol-input100">
								<i class="fa fa-lock" aria-hidden="true"></i>
							</span>
						</div>

						<div class="container-login100-form-btn">
							<button class="login100-form-btn">
								Login
							</button>
						</div>

						<div class="text-center p-t-12">
							<span class="txt1">
								Forgot
							</span>
							<a class="txt2" href="#">
								Username / Password?
							</a>
						</div>

						<div class="text-center p-t-136">
							<a class="txt2" href="#">
								Create your Account
								<i class="fa fa-long-arrow-right m-l-5" aria-hidden="true"></i>
							</a>
						</div>
					</form>
				</div>
			</div>
		</div>
	);
}
